import React, { Component } from 'react';

class EmployeeAdd extends Component{
    initialState = {
        id: '',
        name: '',
        job: '',
        date: '', 
        department: '',
        department1: '',
    };

    constructor(props){
        super(props);
        this.state = {value: 'design'}

        this.handleChange = this.handleChange.bid(this);
        this.handleSubmit = this.handleSubmit.bid(this);
    }

    handleChange(event){
        this.setState({value: event.target.value});
    }


    state = this.initialState;

    handleChange = e => {
        const { name , value } = e.target;
        this.setState({
            [name]: value,
        });

    };

    handleSubmit = (e) => {
       e.preventDefault();
       const {hire} = this.props; 
       hire(this.state);
       this.setState(this.initialState);
    };

    render(){
        const { name, job, id, date, department } = this.state;

        return(
            <form onSubmit={this.handleSubmit}>

                <label htmlFor='job'>Id</label> 
                <input type="text" name="id" id="id" value={id} onChange={this.handleChange}
                placeholder="Id"
                />

                <label htmlFor='name'>Name</label> 
                <input type="text" name="name" id="name" value={name} 
                onChange={this.handleChange}
                placeholder="Name"
                />

                <label htmlFor='job'>Job</label> 
                <input type="text" name="job" id="job" value={job} onChange={this.handleChange}
                placeholder="Job"
                />

                <label htmlFor='date'>Date</label> 
                <input type="text" name="date" id="date" value={date} onChange={this.handleChange}
                placeholder="B-Day"
                />

                <label htmlFor='department'>Department</label> 
                <select name="department" value={this.state.value} onChange={this.handleChange}>
                    <option value="design" name="design">Design</option>
                    <option>Front-end</option>
                    <option>QA</option>
                </select>


                <input type="submit" value="Hire" />
            </form>
        );
    }

}

export default EmployeeAdd;
