import React, { Component } from 'react';
import Employee from './Employee';

class EmployeeList extends React.Component{
    state = {
        employees : [
            {   
                id: 1,
                name: 'John',
                job: 'CEO'
            },
            {
                id: 2,
                name: 'Jane',
                job: 'Manager'
            },
            {
                id: 3,
                name: 'Michelle',
                job: 'Programer'
            },
            {
                id: 4,
                name: 'Tim',
                job: 'Designer'
            },
        ]
    };

    fireEmployee  = (id) => {
        const { employees } = this.state;
