            })
        });
    };

    render(){

        return (
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Job</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map(employee => (
                            <Employee name={employee.name} job={employee.job} />
                        ))}
                </tbody>
            </table>
        );
    }
}

export default EmployeeList;
